const availableAgents = $('Context').item.json.availableTranscribers;

const currentAgent = $input.item.json.value;
const contextData = $input.first().json;

// Find current agent display name
const currentAgentDisplay = availableAgents.find(
  a => a.provider === currentAgent.provider && a.llm === currentAgent.llm
)?.displayName || 'Unknown';

// Create inline keyboard - array of rows, each row is an array of buttons
const inlineKeyboard = availableAgents.map(agent => {
  const isCurrentAgent = currentAgent.provider === agent.provider && currentAgent.llm === agent.llm;
  const buttonText = isCurrentAgent ? `${agent.emoji} ${agent.displayName} âœ“` : `${agent.emoji} ${agent.displayName}`;

  return [{
    text: buttonText,
    callback_data: `transcriber#${agent.provider}#${agent.llm}`
  }];
});

const messageText = `<b>Current Transcriber:</b> ${currentAgentDisplay}\n\nSelect a new transcriber:`;

// Create reply_markup as JSON string for Telegram API
const replyMarkup = JSON.stringify({
  inline_keyboard: inlineKeyboard
});

return {
  json: {
    chat_id: contextData.chat_id,
    message_id: contextData.message_id,
    user_id: contextData.user_id,
    workflow_id: contextData.workflow_id,
    current_agent: currentAgent,
    request_body:  {
      chat_id : $('Context').first().json.chat_id,
      text: messageText,
      parse_mode: "HTML",
      reply_markup: replyMarkup
  }
}
};

