const callbackData = $('Context').item.json.callback_query_data;
const parts = callbackData.split('#');

if (parts.length === 3 && (parts[0] === 'transcriber' || parts[0] === 'responder') ) {
  const provider = parts[1];
  const llm = parts[2];

  const contextData = $('Context').item.json;

  return {
    json: {
      ...contextData,
      new_agent: {
        provider: provider,
        llm: llm
      },
      new_agent_json: JSON.stringify({provider: provider, llm: llm})
    }
  };
}

throw new Error('Invalid callback data format');

